%% Fit: 'sine fit'.
[xData, yData] = prepareCurveData( Length, Capacity_voltage_mv );

% Set up fittype and options.
ft = fittype( 'sin1' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.Lower = [-Inf 0 -Inf];
opts.StartPoint = [4.53620619514153 0.314159265358979 0.464849690648496];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Create a figure for the plots.
